// Security: HTML escape function to prevent XSS
function escapeHtml(text) {
  if (!text) return '';
  var div = document.createElement('div');
  div.textContent = text;
  return div.innerHTML;
}

// Security: Sanitize URL
function sanitizeUrl(url) {
  if (!url) return '#';
  try {
    var parsed = new URL(url, window.location.origin);
    if (parsed.protocol === 'http:' || parsed.protocol === 'https:') {
      return url;
    }
  } catch(e) {}
  return '#';
}

// Breaking News Ticker
function loadTicker(containerId, label, count) {
  var container = document.getElementById(containerId);
  if (!container) {
    setTimeout(function() { loadTicker(containerId, label, count); }, 100);
    return;
  }
  var script = document.createElement('script');
  label = (label && label.trim() !== '') ? label.trim() : '';
  var url = label ? '/feeds/posts/default/-/' + encodeURIComponent(label) : '/feeds/posts/default';
  script.src = url + '?alt=json-in-script&max-results=' + count + '&callback=renderTicker_' + containerId.replace(/-/g,'_');
  document.head.appendChild(script);

  window['renderTicker_' + containerId.replace(/-/g,'_')] = function(data) {
    var container = document.getElementById(containerId);
    if (!container) return;
    var html = '';
    var entries = data.feed.entry || [];
    // Duplicate for infinite scroll effect
    for (var loop = 0; loop < 2; loop++) {
      for (var i = 0; i < entries.length; i++) {
        var entry = entries[i];
        var title = escapeHtml(entry.title.$t);
        var url = '';
        for (var j = 0; j < entry.link.length; j++) {
          if (entry.link[j].rel === 'alternate') { url = sanitizeUrl(entry.link[j].href); break; }
        }
        html += '<span class="ticker-item"><a href="' + url + '">' + title + '</a></span>';
      }
    }
    container.innerHTML = html;
  };
}

// Hero Slider
function loadSlider(containerId, label, count) {
  var container = document.getElementById(containerId);
  if (!container) {
    setTimeout(function() { loadSlider(containerId, label, count); }, 100);
    return;
  }
  var script = document.createElement('script');
  label = (label && label.trim() !== '') ? label.trim() : '';
  var url = label ? '/feeds/posts/default/-/' + encodeURIComponent(label) : '/feeds/posts/default';
  script.src = url + '?alt=json-in-script&max-results=' + count + '&callback=renderSlider_' + containerId.replace(/-/g,'_');
  document.head.appendChild(script);

  window['renderSlider_' + containerId.replace(/-/g,'_')] = function(data) {
    var container = document.getElementById(containerId);
    if (!container) return;
    var entries = data.feed.entry || [];
    var slidesHtml = '';
    var dotsHtml = '';

    for (var i = 0; i < entries.length; i++) {
      var entry = entries[i];
      var title = escapeHtml(entry.title.$t);
      var url = '', thumb = 'https://via.placeholder.com/800x400?text=No+Image';
      for (var j = 0; j < entry.link.length; j++) {
        if (entry.link[j].rel === 'alternate') { url = sanitizeUrl(entry.link[j].href); break; }
      }
      if (entry.media$thumbnail) {
        thumb = entry.media$thumbnail.url.replace('/s72-c/', '/w800-h450-c/');
      }
      var cat = escapeHtml(entry.category && entry.category[0] ? entry.category[0].term : '');
      var date = new Date(entry.published.$t).toLocaleDateString('id-ID', {day:'numeric',month:'long',year:'numeric'});
      var author = escapeHtml(entry.author[0].name.$t);

      slidesHtml += '<div class="slide' + (i===0?' active':'') + '">';
      slidesHtml += '<img src="' + thumb + '" alt="' + title + '"/>';
      slidesHtml += '<div class="slide-overlay">';
      if (cat) slidesHtml += '<span class="slide-cat">' + cat + '</span>';
      slidesHtml += '<h2 class="slide-title"><a href="' + url + '">' + title + '</a></h2>';
      slidesHtml += '<div class="slide-meta"><i class="far fa-calendar"></i> ' + date + ' &nbsp; <i class="far fa-user"></i> ' + author + '</div>';
      slidesHtml += '</div></div>';

      dotsHtml += '<span class="' + (i===0?'active':'') + '" data-slide="' + i + '"></span>';
    }

    container.innerHTML = slidesHtml + '<div class="slider-dots">' + dotsHtml + '</div>' +
      '<div class="slider-nav"><button class="prev"><i class="fas fa-chevron-left"></i></button><button class="next"><i class="fas fa-chevron-right"></i></button></div>';

    // Slider functionality
    var slides = container.querySelectorAll('.slide');
    var dots = container.querySelectorAll('.slider-dots span');
    var current = 0;

    function showSlide(n) {
      slides[current].classList.remove('active');
      dots[current].classList.remove('active');
      current = (n + slides.length) % slides.length;
      slides[current].classList.add('active');
      dots[current].classList.add('active');
    }

    container.querySelector('.next').onclick = function() { showSlide(current + 1); };
    container.querySelector('.prev').onclick = function() { showSlide(current - 1); };
    dots.forEach(function(dot, i) {
      dot.onclick = function() { showSlide(i); };
    });

    setInterval(function() { showSlide(current + 1); }, 5000);
  };
}

