      if (cat) html += '<span class="post-cat">' + cat + '</span>';
      html += '<div class="post-title"><a href="' + url + '">' + title + '</a></div>';
      html += '</div></div>';
    }

    container.innerHTML = html;
  };
}

// Label Feed - Mixed Layout (3 grid + 2 list = 5 posts)
function labelFeed(containerId, label, count) {
  var container = document.getElementById(containerId);
  if (!container) {
    setTimeout(function() { labelFeed(containerId, label, count); }, 100);
    return;
  }
  if (!label || label.trim() === '') {
    container.innerHTML = '<p style="color:#777;padding:20px;">Silakan isi nama label di konten widget</p>';
    return;
  }
  var safeId = containerId.replace(/-/g,'_');
  var script = document.createElement('script');
  script.src = '/feeds/posts/default/-/' + encodeURIComponent(label.trim()) + '?alt=json-in-script&max-results=5&callback=renderLabel_' + safeId;
  document.head.appendChild(script);

  window['renderLabel_' + containerId.replace(/-/g,'_')] = function(data) {
    var container = document.getElementById(containerId);
    if (!container) return;
    var entries = data.feed.entry || [];
    var html = '';

    // Top 3 posts in grid
    html += '<div class="cat-grid">';
    for (var i = 0; i < Math.min(3, entries.length); i++) {
      var entry = entries[i];
      var title = escapeHtml(entry.title.$t);
      var url = '', thumb = 'https://via.placeholder.com/300x200?text=No+Image';
      for (var j = 0; j < entry.link.length; j++) {
        if (entry.link[j].rel === 'alternate') { url = sanitizeUrl(entry.link[j].href); break; }
      }
      if (entry.media$thumbnail) {
        thumb = entry.media$thumbnail.url.replace('/s72-c/', '/w350-h220-c/');
      }
      var cat = escapeHtml(entry.category && entry.category[0] ? entry.category[0].term : '');

      html += '<div class="cat-grid-item">';
      html += '<div class="cat-grid-thumb"><a href="' + url + '"><img src="' + thumb + '" alt="' + title + '"/></a></div>';
      html += '<h3 class="cat-grid-title"><a href="' + url + '">' + title + '</a></h3>';
      if (cat) html += '<span class="cat-label">' + cat + '</span>';
      html += '</div>';
    }
    html += '</div>';

    // Next 2 posts in list with large image
    if (entries.length > 3) {
      html += '<div class="cat-list">';
      for (var i = 3; i < Math.min(5, entries.length); i++) {
        var entry = entries[i];
        var title = escapeHtml(entry.title.$t);
        var url = '', thumb = 'https://via.placeholder.com/400x250?text=No+Image';
        for (var j = 0; j < entry.link.length; j++) {
          if (entry.link[j].rel === 'alternate') { url = sanitizeUrl(entry.link[j].href); break; }
        }
        if (entry.media$thumbnail) {
          thumb = entry.media$thumbnail.url.replace('/s72-c/', '/w400-h280-c/');
        }
        var cat = escapeHtml(entry.category && entry.category[0] ? entry.category[0].term : '');
        var date = new Date(entry.published.$t).toLocaleDateString('id-ID', {day:'numeric',month:'long',year:'numeric'});

        html += '<div class="cat-list-item">';
        html += '<div class="cat-list-thumb"><a href="' + url + '"><img src="' + thumb + '"/></a></div>';
        html += '<div class="cat-list-info">';
        html += '<h3 class="cat-list-title"><a href="' + url + '">' + title + '</a></h3>';
        if (cat) html += '<span class="cat-label">' + cat + '</span>';
        html += '<span class="cat-date"><i class="far fa-clock"></i> ' + date + '</span>';
        html += '</div></div>';
      }
      html += '</div>';
    }

    container.innerHTML = html;
  };
}

