// Trending Posts
function trendingPosts(containerId, count) {
  var container = document.getElementById(containerId);
  if (!container) {
    setTimeout(function() { trendingPosts(containerId, count); }, 100);
    return;
  }
  var script = document.createElement('script');
  script.src = '/feeds/posts/default?alt=json-in-script&max-results=' + count + '&orderby=updated&callback=renderTrending_' + containerId.replace(/-/g,'_');
  document.head.appendChild(script);

  window['renderTrending_' + containerId.replace(/-/g,'_')] = function(data) {
    var container = document.getElementById(containerId);
    if (!container) return;
    var entries = data.feed.entry || [];
    var html = '';

    for (var i = 0; i < entries.length; i++) {
      var entry = entries[i];
      var title = escapeHtml(entry.title.$t);
      var url = '';
      for (var j = 0; j < entry.link.length; j++) {
        if (entry.link[j].rel === 'alternate') { url = sanitizeUrl(entry.link[j].href); break; }
      }

      html += '<div class="trending-item">';
      html += '<span class="trending-num">' + (i+1) + '</span>';
      html += '<div class="trending-info">';
      html += '<div class="post-title"><a href="' + url + '">' + title + '</a></div>';
      html += '</div></div>';
    }

    container.innerHTML = html;
  };
}
